import axios from 'axios';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';

const WeatherGraph = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch("http://apis.data.go.kr/1360000/VilageFcstInfoService_2.0/getVilageFcst?ServiceKey=#### API SERVICE KEY ####&pageNo=1&numOfRows=290&dataType=JSON&base_date=20230502&base_time=2300&nx=55&ny=128")
      .then(response => response.json())
      .then(json => json.response.body.items.item)
      .then(items => items.filter(item => item.fcstDate === '20230503' && item.category === 'POP'))
      .then(filteredItems => setData(filteredItems.map(item => ({name: item.fcstTime, value: item.fcstValue}))))
  }, [])

  return (
    <LineChart
      width={500}
      height={300}
      data={data}
      margin={{
        top: 5,
        right: 30,
        left: 20,
        bottom: 5,
      }}
    >
      <CartesianGrid strokeDasharray="3 3" />
      <XAxis dataKey="name" />
      <YAxis />
      <Tooltip />
      <Legend />
      <Line type="monotone" dataKey="value" stroke="#8884d8" activeDot={{ r: 8 }} />
    </LineChart>
  );
}

export default WeatherGraph;
